'use strict';

var describe = require('mocha').describe;
var it = require('mocha').it;
var expect = require('chai').expect;
var consoleStringify = require('./console-stringify');

describe('consoleStringify', function () {
  it('should stringify number', function () {
    expect(consoleStringify(1)).to.eq('1');
  });

  it('should stringify object', function () {
    expect(consoleStringify({ a: 'foo' })).to.eq("{ a: 'foo' }");
  });
});